import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function Order({ currentUser }) {
  const [items, setItems] = useState('');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const placeOrder = async () => {
    if (!items.trim()) {
      alert('Please enter items to order');
      return;
    }
    
    setLoading(true);
    try {
      await axios.post('http://localhost:5002/api/orders', { 
        userId: currentUser.userId, 
        items: items.split(',').map(item => item.trim()).filter(item => item) 
      });
      alert('Order Placed!');
      setItems('');
      fetchOrders();
    } catch (error) {
      alert('Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const fetchOrders = async () => {
    try {
      const res = await axios.get('http://localhost:5002/api/orders');
      setOrders(res.data);
    } catch (error) {
      console.error('Failed to fetch orders:', error);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="order-container">
      <div className="order-form">
        <h2>Place Order</h2>
        <div className="form-group">
          <label>Items (comma-separated):</label>
          <input 
            placeholder="e.g., Burger, Fries, Coke" 
            value={items}
            onChange={e => setItems(e.target.value)} 
          />
        </div>
        <button 
          onClick={placeOrder} 
          disabled={loading}
          className="order-button"
        >
          {loading ? 'Placing Order...' : 'Place Order'}
        </button>
      </div>

      <div className="orders-list">
        <h3>All Orders:</h3>
        {orders.length === 0 ? (
          <p>No orders yet</p>
        ) : (
          orders.map((order, index) => (
            <div key={order._id || index} className="order-item">
              <p><strong>Items:</strong> {order.items.join(', ')}</p>
              <p><strong>Status:</strong> <span className={`status-${order.status}`}>{order.status}</span></p>
              <p><strong>Total:</strong> ${order.totalAmount}</p>
              {order.userId && order.userId.username && (
                <p><strong>Ordered by:</strong> {order.userId.username}</p>
              )}
            </div>
          ))
        )}
      </div>

      <div className="navigation">
        <Link to="/" className="nav-button">🏠 Back to Home</Link>
        <Link to="/menu" className="nav-button">📋 View Menu</Link>
        <Link to="/orders" className="nav-button">📦 View Orders</Link>
      </div>
    </div>
  );
}

export default Order;
